import React,{useState,useEffect} from "react";
import { Link,useLocation,useHistory } from "react-router-dom";
import { Container, Grow, Grid , Button, Typography,Input } from '@material-ui/core';
import DemoCarousel from '../logged.js';
import { v4 as uuid } from "uuid";



const intialState={roomid:''};
export const Home =()=>{
  

  const [roomID,setRoomID] = useState(intialState);
  const [user,setUser] = useState(JSON.parse(localStorage.getItem('profile')));
  const location = useLocation();
  const history = useHistory();

  const clickHere = () => {
        sessionStorage.setItem('user', user.result.username);
        
        history.push('/room');
    }


  const enterRoom = () => {
    if (!roomID.roomid) {
      console.log('Enter Room ID please !! ');
    } else {
      
      sessionStorage.setItem('user', user.result.username);
      
      history.push(`/room/${roomID.roomid}`);
    }
  }
  

  const handleChange= (e)=> {
    setRoomID({ ...roomID, [e.target.name]: e.target.value });
    console.log(roomID.roomid);
};
  useEffect(()=>{
    
    setUser(JSON.parse(localStorage.getItem('profile')));
  },[history,location]);
  
    return(
      <Container maxWidth="lg">
        <Grow in>
          <Container>
            <Grid container justifyContent="space-between" alignItems="stretch" spacing={3}>
              <Grid item xs={12} sm={7}>
                {user ? (
                  <Grid >
                  <Typography >            
                    <Button  variant="contained" color="primary" onClick={createRoom}>Create room</Button>
                    </Typography>
                    <Button  onClick={clickHere}>Click here</Button>
                    <Typography >
                    
                    <Input name="roomid" label="Enter Room" onChange={handleChange} autofocus half />
                    <Button  type="submit" variant="contained" color="primary" onClick={enterRoom}>Join</Button>  
                    </Typography >
                    
                    </Grid>  
                  ) : (
                  <div >
                  <h3 className="">Welcome to Garage d'Etude!</h3>
                  <h5 className="">Garage d'Etude is a platform in which you can study or teach online!</h5>
                  <br></br>
                  <br></br>
                  <Button component={Link} to="/auth" variant="contained">start your class </Button>
                  </div> 
                )
                }             
              </Grid>
              <Grid item xs={12} sm={4}>
                  <DemoCarousel/>
              </Grid>
            </Grid>
          </Container>
        </Grow>
      </Container>
  )
};
